// Extract and validate chat message data
const body = $input.first().json.body;

if (!body || !body.message) {
  throw new Error('Missing required message field');
}

const chatContext = {
  message: body.message,
  companyId: body.companyId || 'default',
  selectedEvent: body.selectedEvent || null,
  recentEvents: body.recentEvents || [],
  financialMetrics: body.financialMetrics || {},
  eventGraph: body.eventGraph || { nodes: [], edges: [] },
  timestamp: new Date().toISOString()
};

// Prepare context for AI processing
const contextSummary = {
  company: chatContext.companyId,
  eventCount: chatContext.recentEvents.length,
  hasFinancialData: Object.keys(chatContext.financialMetrics).length > 0,
  hasSelectedEvent: chatContext.selectedEvent !== null,
  graphNodeCount: chatContext.eventGraph.nodes.length
};

return {
  chatContext,
  contextSummary,
  userMessage: chatContext.message
};

// Process AI response and extract event references
const aiResponse = $input.first().json.choices[0].message.content;
const chatContext = $('context-processor').first().json.chatContext;

// Extract event references from response
const eventReferencePattern = /\[Event: ([^\]]+)\]/g;
const linkedEvents = [];
let match;

while ((match = eventReferencePattern.exec(aiResponse)) !== null) {
  linkedEvents.push(match[1]);
}

// Format response with metadata
const formattedResponse = {
  id: `msg_${Date.now()}`,
  content: aiResponse,
  timestamp: new Date().toISOString(),
  isUser: false,
  linkedEvents: linkedEvents,
  context: {
    companyId: chatContext.companyId,
    processedAt: new Date().toISOString(),
    eventReferencesFound: linkedEvents.length
  }
};

// Prepare webhook response
const webhookResponse = {
  success: true,
  message: formattedResponse,
  linkedEvents: linkedEvents,
  processingTime: Date.now() - new Date(chatContext.timestamp).getTime()
};

return webhookResponse;
